import React, { Component } from 'react';

class QuienPagoAddModal extends Component{
    constructor(props){
        super(props);

        this.onAddPayPerson = this.onAddPayPerson.bind(this);
    }

    onAddPayPerson(){
        var newPayPerson = {
            name: this._inputElement
        }
    }

    render(){
        return(
            <div>
                <div className="modal fade" id="AddPayPerson" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Agregar quién pagó</h5>
                        </div>
                        <div className="modal-body">
                            <input ref={(name) => this._nameInput = name} type="text" placeholder="Nombre..." className="mb-2"/>
                            <input ref={(type) => this._typeInput = type} type="text" placeholder="Qué compró?" className="type-input pl-2 pr-2 align-middle" className="mb-2"/>
                            <input ref={(payed) => this._payedInput = payed} type="number" min="0" className="currency currency-positive text-right" placeholder="Cuánto pagó?" className="mb-2"/>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">cerrar</button>
                            <button type="button" className="btn btn-primary" onClick={this.state.onAddPayPerson}>Agregar</button>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
};

export default QuienPagoAddModal;
