import React, { Component } from 'react';

class ScrollRocker extends Component{
    constructor(props){
        super(props);
        this.myRef = null;
        this.handleScrollTo = this.handleScrollTo.bind(this);
    }

    render(){
        return(
            <div className="scroll-rocker">
                <i className="material-icons i-btn">keyboard_arrow_up</i>
                <i className="material-icons mr-3 i-btn">keyboard_arrow_down</i>
            </div>
        );
    }

    handleScrollTo(ref){
        window.scrollTo({
            top: this.props.scrollAnchor,
            behavior: "smooth"
        })
    }
}

export default ScrollRocker;
